  constructor(props){
    super(props)
    this.state = {
      task:{}
    }
    this.actions ={
      addTask: this.addTask
    }
  }

  addTask = (title,text) =>{
    this.setState(state=> produce(state, draft=>{
      // let qnt = 'quantity'
      console.log('DRAFT______', task)
      console.log('DRAFT-----', draft.task[title])
      //pid
      if(!draft.cart[title]){
          console.log('data in add to cart->',draft.task[title])
          draft.cart[title] = 1
          console.log('data in add to cart->',draft.task[title])


      }else{
          // product[qnt]++
          console.log('draft.cart[pid].quantity',draft.task[title].desc)
          draft.cart[title]++
      }
  }))


  }
  render=()=>{
    return (
      <View style={styles.container}>
        <Text>Open up App.js to start working on your app!</Text>
        <ListItem state={this.state} action={this.actions}/>
      </View>
    );
  }
}








