import React from 'react';
import { StyleSheet, Text, View,Button } from 'react-native';
import { Icon } from 'react-native-elements'
import ListItem from './comps/ListItem'
import produce from 'immer'

export default class App extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      task:{}
    }
    this.actions ={
      addTask: this.addTask
    }
  }

  addTask = (title,text) =>{
    this.setState(state=> produce(state, draft=>{
      // let qnt = 'quantity'
      console.log('DRAFT______', title)
      console.log('DRAFT-----', draft.task[title])
      //pid
      if(!draft.task[title]){
          console.log('data in add to cart->',draft.task[title])
          draft.task[title] = text
          console.log('data in add to cart->',draft.task[title])


      }else{
          draft.task[title] = text
      }
  })) 

  }
  removeTask = (title) =>{
    console.log('inthe removeTask---------->------>')
    this.setState(state=>produce(state,draft =>{
        delete draft.task[title]
    }))


  }
  render=()=>{
    return (
      <View style={styles.container}>
        <Text style={styles.header}>Welcome to the Minimal To-Do List React Native App!</Text>
        <ListItem state={this.state} action={this.actions}/>
        {Object.entries(this.state.task).map(item=>{
          return(
            <View key={item[0]+'card'} style={styles.card}>
              <View key={item[0]+'title'} style={styles.cardHeader}>

                <Text style={{width: '50%'}}>Title: {item[0]}            
                </Text >
                <Text  onPress={()=>this.removeTask(item[0])}>
                  <Icon name="delete"></Icon>
                </Text>
              </View>
              <View key={item[0]+'body'} style={{backgroundColor:'whitesmoke'}}>
                <Text>
                  Description: {item[1]}
                </Text>
                <View>
                  <Button
                        onPress={()=>this.removeTask(item[0])}
                        title="remove task"
                        color="#009900"
                        style={{margin:'.5rem'}}
                    />                
                </View>
              </View>      
            </View>
         )
        })}
      </View>
    );
  }
}








const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    margin: '1rem',
    backgroundColor: 'slategray',

  },
  row: {
    margin: '1rem',
  },
  header:{
    fontSize: '2rem',
    textAlign: "center",
    color: '#fff'
  },
  card:{
    width:'20rem',

  },
  cardHeader:{
    backgroundColor:'lightGray',
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    alignItems: 'flex-start'
  },
  cardBody:{

  }
});
